import React, { Component } from 'react'
import "./Home.css"

export default class Home extends Component {

    constructor(props) {
      super(props)

      this.state = {
         cities:[
             {name:"Casablanca",population:"3,36M"},
             {name:"tanger",population:"947 952K"},
             {name:"marrekech",population:"3928 850K"},
             {name:"rabat",population:"577 827K"},
             {name:"ouarzazte",population:"69 420K"},

         ],
         valueInput:""
      }
    }

    bindValue = (e) =>{
        this.setState({
            valueInput:e.target.value
        })
    }

  render() {
    return (
     <div>
          <header>
          <p>Search of Cities</p>
      </header>
        <div className="row my-2 ">
            <div className="col-md-6 mx-auto">
            <input onChange={this.bindValue} class="form-control" type="text" placeholder="Default input" />
            </div>
        </div>
        <div className="row">
            <div className="col-md-6 mx-auto">
            <table class="table">
                <thead>
                    <tr>
                    <th scope="col">#</th>
                    <th scope="col">City</th>
                    <th scope="col">Population</th>
                    </tr>
                </thead>
                <tbody>
               {
                    console.log(this.state.cities.filter((city) => city.name == "casa" ))
               }



                </tbody>
                </table>
            </div>
        </div>
     </div>

    )
  }
}
